tabyl / adorn

Data1_clean_distinct |&gt;
  tabyl(Year,gender, show_na = F) |&gt;
  adorn_totals("row") |&gt;
  adorn_percentages("row") |&gt;
  adorn_pct_formatting() |&gt;
  adorn_ns() |&gt;
  adorn_title("combined")

