group_by with tabyl

Data1_clean_distinct |&gt;
  group_by(Year,gender) |&gt;
  summarise(Attendances = n()) |&gt;
  filter(!is.na(Year)) |&gt;
  pivot_wider(names_from = gender, values_from = Attendances) |&gt;
  adorn_totals(c("row","col"))

